using ImGuiNET;
using SimulationFramework;
using SimulationFramework.Drawing;
using SimulationFramework.Input;
using System.Numerics;

//almost every algebraic chess notation :P


namespace viylouuInc_Launcher
{
    internal class TinkGame
    {
        public bool started = false;

        public ITexture rogueidle = null;

        public ITexture ovwenv = null;

        public int pixSize = 6;

        public bool men = false;

        public Vector2[,] ovwmap = new Vector2[,] {
            {Vector2.Zero, new Vector2(32, 16), new Vector2(32, 16), new Vector2(32, 16), Vector2.Zero},
            {new Vector2(32, 16), new Vector2(32, 16), new Vector2(32, 16), new Vector2(32, 16), new Vector2(32, 16)},
            {new Vector2(32, 16), new Vector2(32, 16), new Vector2(32, 16), new Vector2(32, 16), new Vector2(32, 16)},
            {new Vector2(32, 16), new Vector2(32, 16), new Vector2(32, 16), new Vector2(32, 16), new Vector2(32, 16)},
            {Vector2.Zero, new Vector2(32, 16), new Vector2(32, 16), new Vector2(32, 16), Vector2.Zero}
        };

        public void Update()
        {
            if (!started)
            {
                if (rogueidle != null) { rogueidle.Dispose(); }
                rogueidle = Graphics.LoadTexture(@"Assets\Sprites\Tink Assets\Entities\Players\Rogue\Idle-Sheet.png");
                if (ovwenv != null) { ovwenv.Dispose(); }
                ovwenv = Graphics.LoadTexture(@"Assets\Sprites\Tink Assets\Woods-Overworld\Tiles.png");

                started = true;
            }
            else
            {
                if (Keyboard.IsKeyPressed(Key.Esc))
                { men = !men; }

                if (men)
                {
                    ImGui.Begin("Menu");

                    if (ImGui.Button("Quit Game"))
                    {
                        Program.gameStarted = false;
                        started = false;
                    }

                    if (ImGui.Button("Quit Launcher"))
                    {
                        Environment.Exit(0);
                    }

                    ImGui.End();
                }

                ICanvas canv = Graphics.GetOutputCanvas();

                canv.Clear(Color.Black);

                for (int x = 0; x < ovwmap.GetLength(0); x++)
                {
                    for (int y = 0; y < ovwmap.GetLength(1); y++)
                    {
                        canv.DrawTexture(
                            ovwenv,
                            new Rectangle(
                                new Vector2(
                                    ovwmap[x, y].X,
                                    ovwmap[x, y].Y
                                ),
                                new Vector2(
                                    16,
                                    16
                                ),
                                Alignment.TopLeft
                            ),
                            new Rectangle(
                                new Vector2(
                                    canv.Width / 2 + (x - (ovwmap.GetLength(0) / 2)) * 16 * pixSize,
                                    canv.Height / 2 + (y - (ovwmap.GetLength(1) / 2)) * 16 * pixSize
                                ),
                                new Vector2(
                                    16 * pixSize,
                                    16 * pixSize
                                ),
                                Alignment.Center
                            )
                        );
                    }
                }

                canv.DrawTexture(
                    rogueidle,
                    new Rectangle(
                        new Vector2(
                            0,
                            0
                        ),
                        new Vector2(
                            32,
                            32
                        ),
                        Alignment.TopLeft
                    ),
                    new Rectangle(
                        new Vector2(
                            canv.Width / 2,
                            canv.Height / 2
                        ),
                        new Vector2(
                            32 * pixSize,
                            32 * pixSize
                        ),
                        Alignment.Center
                    )
                );
            }
        }
    }
}
